	c.legacyFontAwesome =function(){
		var url = "https://dev25300.service-now.com/fontawesome_sp/?id=fontawesome_home_page&sysparm_domain_restore=false&sysparm_stack=no#collapseOne1";

		var popup = OpenPopupCenter(url, "Fontawesome", "width=300, height=280");

		// var popup =  window.open (url, "popup", "width=900, height=600");
	}
	function OpenPopupCenter(pageURL, title, w, h) {
		var left = (screen.width - w) / 2;
		var top = (screen.height - h) / 3;  // for 25% - devide by 4  |  for 33% - devide by 3
		var targetWin = window.open(pageURL, title, 'toolbar=no, location=no, directories=no, status=no, menubar=no, scrollbars=no, resizable=no, copyhistory=no, width=' + w + ', height=' + h + ', top=' + top + ', left=' + left);
     //var targetWin = window.open(pageURL, title, "scrollbars=no,width=300,height=280,resizable=yes");
	} 
