sp_widget.client_script:
function($rootScope, $scope, spUtil) {
	/* widget controller */
	var c = this;
	//--------------------------------------------------------------------------------
	// Prepare the record to insert ne user
	//--------------------------------------------------------------------------------
	c.newRec = function() {
		c.server.get({
			action: 'newRec'
		}).then(function(r) {
			// save newly created unique sys_id
			c.data.recID = r.data.recID;
		});
	}

	//--------------------------------------------------------------------------------
	// Update newly created record based on the unique sys_id
	//--------------------------------------------------------------------------------
	$scope.addUser = function (userDetails) {

		//  this line of code is just for debigging use
		$scope.message = userDetails.name + " ( " + userDetails.email + " ) ( " + userDetails.agreed + " ) ";
		//spUtil.addInfoMessage($scope.message);

		c.newRec();// get sys_id

		//call the server and update this newly created sys_id
		spUtil.addInfoMessage(c.data.recID);
		c.server.get({
			action: 'updateNewRec',
			newRecID: c.data.recID,
			fullName: userDetails.name,
			userEmail: userDetails.email 
			// add more fields ..

		}).then(function(r) {
			// used to updated the view if needed
			//r.data 
		});
	}

	$scope.message = "Ready";

}//End function($rootScope, $scope, spUtil)

sp_widget.script:
(function() {
	if (input) {
		//----------------------------------------------------------------------
		// Prepare the record and get default values
		//----------------------------------------------------------------------
		var newUser;
		if (input.action == 'newRec') {
			newUser = new GlideRecord('x_snc_createnotes_note');
			newUser.newRecord();
			var newRecSysID = newUser.insert();// return sys_id
			data.recID = newRecSysID;
			data.newRecJSON = {};
			$sp.getRecordValues(data.newRecJSON,newUser,"title,note,sys_id,user_name,user_email");  // 
		}

		if(input.action == 'updateNewRec') { 
			    newUser = new GlideRecord('x_snc_createnotes_note');
					//var sysID = input.newRecID; 
					newUser.user_name = input.fullName; 
			    newUser.user_email = input.userEmail; 
			    newUser.title = "Update";
					newUser.note = "Worked";
			    // remap fields ... 
					newUser.update(); 
		}
	}
}
)();
